    if(No_of_units <= 50)
    {
        Amount = 0;
    }
    else if(No_of_units <= 150)
    {
        Amount = (No_of_units - 50) * 0.80;
    }
    else if(No_of_units <= 350)
    {
        Amount = 80 + (No_of_units - 150) * 1.00;
    }
    else
